package daytwo;

public class Arith {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=7;
		int b=3;
		
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);

	}

}
